
public class for_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0; i<20;i++) {
			System.out.println(i);
		}
		
		int sum= 0;
		for (int n=0;n<100;n++) {
			sum=sum+n;
		}
		System.out.println("Sum of first 100 number is " + sum);
		
		}

	}


