
public class variable_reference {
	    static int age;
		static int salary;
        static int house;
        static String name;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
			 int age=4;
			 int salary=6000;
			 int house=2;
			 String name="jack";
		
		System.out.println(age);
		System.out.println(salary);
		System.out.println(house);
		System.out.println(name);
	}

}
